import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Rectangle } from '../models/rectangle';

@Injectable({
  providedIn: 'root'
})
export class RectangleService {
  private filePath = '../../../dimensions.json';
  private apiUrl = 'https://localhost:7193/api/Rectangles/SaveDimensions';

  constructor(private http: HttpClient) { }

  SaveRectangle(rectangle: Rectangle) : any{
    this.http.post(this.apiUrl, rectangle).subscribe(res => {
      console.log("Api Post res:")
      console.log(res)
    });
  }

  SaveJson(rectangle: Rectangle) : any{
    this.http.post(this.filePath, rectangle).subscribe(res => {
      console.log("Save Json res:")
      console.log(res)
    });
  }

  GetRectangle() : Observable<Rectangle>{
    return this.http.get<Rectangle>(this.filePath);
  }
}
