import { Component } from '@angular/core';
import { Rectangle } from './models/rectangle';
import { RectangleService } from './services/rectangle.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'DemoApp';
  rectangle = new Rectangle();

  constructor(private rectangleService : RectangleService){}

  ngOnInit() :void {
    this.rectangleService.GetRectangle().subscribe(rectangle=>{
      this.rectangle = rectangle;
      this.rectangle.width = 30;
      this.rectangleService.SaveRectangle(rectangle).subscribe(result=>{
        if(result.success){
          this.rectangle = result.data as Rectangle;
          console.log(this.rectangle)

          this.rectangleService.SaveJson(this.rectangle)
        }
      })
    });
  }
}
